class Solution {
    public int beautifulNumbers(int l, int r) {
        DigitalDp digitalDp = new DigitalDp();
        long ans = digitalDp.dfs(String.valueOf(r), 0, 0, true, true);
        DigitalDp digitalDp2 = new DigitalDp();
        ans -= digitalDp2.dfs(String.valueOf(l - 1), 0, 0, true, true);
        return (int) ans;
    }

    public class DigitalDp {

        private Map<Long, Long> map = new HashMap<>();

        // 0n2(0n)
        // idx0num.charAt(idx)
        // isLimit
        // allZero0
        public long dfs(String num, int idx, long status, boolean isLimit, boolean allZero) {
            long key = getKey(idx, status, isLimit, allZero);
            if (map.containsKey(key)) {
                return map.get(key);
            }
            // 
            if (idx == num.length()) {

                // 
                long chengji = status / 200;
                long he = status % 200;
                if (he !=0 && chengji % he == 0) {
                    map.put(key, 1L);
                    return 1L;
                } else {
                    map.put(key, 0L);
                }
                return 0L;
            }
            int up = num.charAt(idx) - '0';
            int max = isLimit ? up : 9;
            long count = 0;
            for (int i = 0; i <= max; i++) {
                boolean nextLimit = isLimit && up == i;
                boolean nextAllZero = allZero & i == 0;

                long he = status % 200;
                long chengji = status / 200;
                // TODO status,newStatus
                long newHe = he + i;
                // 
                boolean jia = allZero; // 0
                long newChengji = jia ? chengji + i : chengji * i;
                long newStatus = getStatus(newChengji, newHe);
                count += dfs(num, idx + 1, newStatus, nextLimit, nextAllZero);
            }
            map.put(key, count);
            return count;
        }

        private long getStatus(long chengji, long he) {
            return chengji * 200 + he;
        }

        private long getKey(int idx, long status, boolean isLimit, boolean allZero) {
            // TODO status
            long res = idx * 1000000000L * 2 * 2;
            res += status * 2 * 2;
            res += isLimit ? 2 : 0;
            res += allZero ? 1 : 0;
            return res;
        }
    }
}

