fn solve(n: u64) -> u64 {
    if n == 0 {
        return 0;
    }

    let ub: Vec<u8> = n.to_string().chars().map(|c| c as u8 - b'0').collect();
    let m = ub.len();
    let max_sum = 9 * m;
    let mut ans = 0;

    for s in 1..=max_sum {
        let mut lt = vec![vec![0u64; s]; s + 1];
        let mut eq = vec![vec![0u64; s]; s + 1];
        eq[0][0] = 1;

        for i in 0..m {
            let mut nlt = vec![vec![0u64; s]; s + 1];
            let mut neq = vec![vec![0u64; s]; s + 1];

            for j in 0..=s {
                for d in 0..10 {
                    if j + d > s {
                        break;
                    }
                    for k in 0..s {
                        if j == 0 {
                            nlt[j + d][d % s] += lt[j][k];
                        } else {
                            nlt[j + d][k * d % s] += lt[j][k];
                        }
                        if d < ub[i] as usize {
                            if j == 0 {
                                nlt[j + d][d % s] += eq[j][k];
                            } else {
                                nlt[j + d][k * d % s] += eq[j][k];
                            }
                        }
                        if d == ub[i] as usize {
                            if j == 0 {
                                neq[j + d][d % s] += eq[j][k];
                            } else {
                                neq[j + d][k * d % s] += eq[j][k];
                            }
                        }
                    }
                }
            }

            lt = nlt;
            eq = neq;
        }

        ans += lt[s][0] + eq[s][0];
    }
    ans
}

impl Solution {
    pub fn beautiful_numbers(l: i32, r: i32) -> i32 {
        (solve(r as u64) - solve((l as u64).saturating_sub(1))) as i32
    }
}

