class Solution {
public:
    int beautifulNumbers(int l, int r) {
        string low = to_string(l);
        string high = to_string(r);
        int n = high.size();
        low = string(n - low.size(), '0') + low; //  high 

        vector<unordered_map<pair<int, int>, int>> memo(n);

        function<long long(int, int, int, bool, bool)> dfs = [&](int i, int mul, int cs, bool limit_low, bool limit_high) -> long long {
            if (i == n) {
                return mul % cs == 0;
            }

            if (!limit_low && !limit_high && memo[i].count({ mul, cs })) {
                return memo[i][{ mul, cs }];
            }

            //  i  lo  hi
            //  for  lo  hi
            int lo = limit_low ? low[i] - '0' : 0;
            int hi = limit_high ? high[i] - '0' : 9;

            long long ans = 0;
            for (int d = lo; d <= hi; ++d) {
                if (cs == 0 && d == 0)
                    ans += dfs(i + 1, mul, cs + d, limit_low && d == lo, limit_high && d == hi);
                else
                    ans += dfs(i + 1, mul * d, cs + d, limit_low && d == lo, limit_high && d == hi);
            }

            if (!limit_low && !limit_high) {
                memo[i][{ mul, cs }] = ans;
            }
            return ans;
        };

        return dfs(0, 1, 0, true, true);
    }
};

