func beautifulNumbers(l int, r int) int {
	get := func(n int) int {
		s := strconv.Itoa(n)
		m := len(s)
		type pair struct {
			n1, n2 int
		}
		memo := make([][1 << 10]map[pair]int, m)
		for i := range memo {
			for j := range memo[i] {
				memo[i][j] = make(map[pair]int)
			}
		}
		var f func(int, int, int, int, bool, bool) int
		f = func(i, mask, sm, ss int, isLimit, isNum bool) (res int) {
			if i == m {
				if isNum && sm%ss == 0 {
					return 1 
				}
				return
			}
			pa := pair{n1: sm, n2: ss}
			if !isLimit && isNum {
				if w, ok := memo[i][mask][pa]; ok {
					return w
				}
			}
			if !isNum { 
				res += f(i+1, mask, sm, ss, false, false)
			}
			d := 0
			if !isNum {
				d = 1 
			}
			up := 9
			if isLimit {
				up = int(s[i] - '0') 
			}
			for ; d <= up; d++ { 
				res += f(i+1, mask|1<<d, sm*d, ss+d, isLimit && d == up, true)
			}
			memo[i][mask][pa] = res
			return
		}
		return f(0, 0, 1, 0, true, false)
	}
	return get(r) - get(l-1)
}

