ll dfs(int pos, ll prod, ll sum, int up, int zero) {
    if (pos == -1) {
        if (sum == 0) return 0;
        return prod % sum == 0;
    }
    if (f[pos][sum][up][zero].find(prod) != f[pos][sum][up][zero].end()) {
        return f[pos][sum][up][zero][prod];
    }
    ll& ans = f[pos][sum][up][zero][prod];
    ans = 0;
    for (int x = 0; x <= (up ? a[pos] : 9); x++) {
        ans += dfs(pos - 1, prod * (zero && x == 0 ? 1 : x), sum + x,
                   up && x == a[pos], zero && x == 0);
    }
    return ans;
}

void clear() {
    for (int i = 0; i < 123; i++)
        for (int j = 0; j < 123; j++)
            for (int k = 0; k < 2; k++)
