    int pos = 0;
    while (x) {
        a[pos] = x % 10;
        x /= 10;
        pos++;
    }
    ll ans = dfs(pos - 1, 1, 0, 1, 1);
    // cout << ox << ' ' << ans << endl;
    return ans;
}

