                limit_c, pre_c = limit and j == mx, pre and not j
                ans += dfs(i + 1, sums + j, muls_c, limit_c, pre_c)

            return ans

        ans = dfs(0, 0, 1, True, True)
        return ans


    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.calc(r) - self.calc(l - 1)

