from functools import cache, lru_cache

def fmax(x, y):
    if x > y:
        return x
    return y

def get_digits(val):
    digits = []
    while val:
        digits.append(val % 10)
        val //= 10
    return digits[::-1]

def count_beautiful_numbers(l: int, r: int) -> int:
    return count_beautiful_upto(r) - count_beautiful_upto(l - 1)

def count(n: int) -> int:
    digits = get_digits(n)
    if n == 0:
        return 0
    @cache
    def dfs(ss, pp, idx, is_bound, zero):
        if idx == len(digits):  
            return ss > 0 and (not (pp % ss))
        limit = digits[idx] if is_bound else 9
        total = 0

        for d in range(0, limit + 1):
            new_ss = ss + d
            new_pp = pp * d
            if zero and (not d):
                new_pp = pp
            total += dfs(new_ss, new_pp, idx + 1, is_bound and (d == limit), zero and (not d))

        return total

    return dfs(0, 1, 0, True, True)

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        if l == 1:
            return count(r)
        return count(r) - count(l - 1)

