            if pos >= n:
                return 1 if mx%ms == 0 else 0

            l, r = 0, 9
            if tlo:
                l = lo[pos]
            if thi:
                r = hi[pos]

            res = 0
            for i in range(l,r+1):
                if i > 0:
                    pd = True
                res += dfs(pos+1, tlo and i == l, pd, thi and i == r, mx*i if pd else mx, ms+i)
            return res

        ret = dfs(0, True, False, True, 1, 0)
        dfs.cache_clear()
        return ret

