int dp[10][82][82];
class Solution {
public:
  int beautifulNumbers(int l, int r) {
    string lo = to_string(l), hi = to_string(r);
    int n = (int)hi.size();
    lo = string(n-(int)lo.size(), '0')+lo;
    int ans = 0;
    for (int target = 1; target <= 81; target++) {
      memset(dp, -1, n*sizeof(dp[0]));
      auto dfs = [&] (auto &&self, int i, int prod, int sum, int leading_zero, int lo_limit, int hi_limit) -> int {
        if (i == n) {
          return sum == target && prod == 0;
        }
        if (!leading_zero && !lo_limit && !hi_limit && dp[i][prod][sum] != -1) {
          return dp[i][prod][sum];
        }
        int res = 0;
        int low = (lo_limit ? lo[i]-'0' : 0), hig = (hi_limit ? hi[i]-'0' : 9);
        for (int d = low; d <= hig; d++) {
          res += self(self, i+1, (leading_zero && d == 0 ? prod : prod*d%target), sum+d, leading_zero && d == 0, lo_limit && d == low, hi_limit && d == hig);
        }
        if (!leading_zero && !lo_limit && !hi_limit) dp[i][prod][sum] = res;
        return res;
      };
      ans += dfs(dfs, 0, 1, 0, true, true, true);
    }
    return ans;
  }
};

