class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n):
            if n < 1:
                return 0            
            digits = list(map(int, str(n)))
            length = len(digits)

            @lru_cache(None)
            def dp(pos, tight, sum_digits, product_digits, leading_zero):
                if pos == length:
                    return 1 if sum_digits > 0 and product_digits % sum_digits == 0 else 0
                limit = digits[pos] if tight else 9
                res = 0
                for d in range(0, limit + 1):
                    new_tight = tight and (d == limit)
                    new_sum = sum_digits + d
                    new_product = product_digits * d if not leading_zero else d  
                    res += dp(pos + 1, new_tight, new_sum, new_product, leading_zero and d == 0)
                return res

            return dp(0, True, 0, 1, True)

        return count_beautiful(r) - count_beautiful(l - 1)

