from typing import *


import math


def f(n: int) -> int:
    s = list(map(int, str(n)))
    res = 0
    for m in range(1, 82):
        cm = m
        for i in range(2, 10):
            while cm % i == 0:
                cm //= i
        if cm != 1:
            p = [0] * (2 * (m + 1) * 2)
            for i in range(len(s)):
                q = [0] * (2 * (m + 1) * 2)
                for j in range(2 * (m + 1) * 2):
                    if not p[j]:
                        continue
                    u, w, v = j // 2 // (m + 1), j // 2 % (m + 1), j % 2
                    for d in range(10):
                        if v and d > s[i]:
                            continue
                        uu = u or d == 0
                        ww = w + d
                        vv = v and d == s[i]
                        if ww <= m:
                            q[uu * 2 * (m + 1) + ww * 2 + vv] += p[j]
                for d in range(1, 10):
                    if i == 0 and d > s[i]:
                        continue
                    uu = 0
                    ww = d
                    vv = i == 0 and d == s[i]
                    if ww <= m:
                        q[uu * 2 * (m + 1) + ww * 2 + vv] += 1
                p = q
            res += p[(m + 1) * 2 + m * 2] + p[(m + 1) * 2 + m * 2 + 1]
            continue

        p = [0] * (m * (m + 1) * 2)
        for i in range(len(s)):
            q = [0] * (m * (m + 1) * 2)
            for j in range(m * (m + 1) * 2):
                if not p[j]:
                    continue
                u, w, v = j // 2 // (m + 1), j // 2 % (m + 1), j % 2
                if w + 9 * (len(s) - i) < m:
                    continue
                for d in range(10):
                    if v and d > s[i]:
                        continue
                    uu = math.gcd(u * d, m)
                    if uu == m:
                        uu = 0
                    ww = w + d
                    vv = v and d == s[i]
                    if ww <= m:
                        q[uu * 2 * (m + 1) + ww * 2 + vv] += p[j]
            for d in range(1, 10):
                if i == 0 and d > s[i]:
                    continue
                uu = math.gcd(d, m)
                if uu == m:
                    uu = 0
                ww = d
                vv = i == 0 and d == s[i]
                if ww <= m:
                    q[uu * 2 * (m + 1) + ww * 2 + vv] += 1
            p = q
        res += p[m * 2] + p[m * 2 + 1]
    return res


