namespace dalt {
  template<class T>
  struct Radix {
    Vec<T> base;
    Radix(T x) {
      static const T MAX_VALUE =
          std::numeric_limits<T>::max();
      base.push_back(1);
      T y;
      while((y = MulLimit(base.back(), x, MAX_VALUE, T(0))) != T(0)) {
        base.push_back(y);
      }
    }
    T get(T x, int kth) const {
      if(kth >= Size(base)) {
        return T(0);
      }
      return x / base[kth] % base[1];
    }
    T set(T x, int kth, T val) const {
      return x + (val - get(x, kth)) * base[kth];
    }
  };
}
class Solution
{
public:
  int beautifulNumbers(int l, int r) {
    Radix<int> radix(10);
    int maxSum = 0;
    int numOfDigit = 0;
    for(int i = 0; i < 10; i++) {
      if(radix.get(r, i) > 0) {
        maxSum = 9 * (i + 1);
        numOfDigit = i;
      }
    }
    int ans = 0;
    var mem = MDVecDef<int, 5>::Make(2, 2, numOfDigit + 1, maxSum + 1, maxSum + 1, -1);
    var defaultMem = MDVecDef<int, 5>::Make(2, 2, numOfDigit + 1, maxSum + 1, maxSum + 1, -1);
    for(int mod = 1; mod <= maxSum; mod++) {
      mem = defaultMem;
      var dp = [&](var &rec, int sum, int remainder, int index, int c, int f) -> int {
        if(index < 0) {
          return int(remainder == 0 && sum == mod);
        }
        if(sum > mod) {
          return 0;
        }
        //DebugFmtln("dp(%d, %d, %d, %d, %d, %d)", mod, sum, remainder, index, c, f);
        var &ans = mem[c][f][index][remainder][sum];
        if(ans == -1) {
          ans = 0;
          int lval = radix.get(l, index);
          int rval = radix.get(r, index);
          for(int i = 0; i < 10; i++) {
            if(lval > i && f) {
              continue;
            }
            if(rval < i && c) {
              continue;
            }
            ans += rec(rec, sum + i, sum + i == 0 ? 1 : (remainder * i % mod), index - 1, c && rval == i, f && lval == i);
          }
          DebugFmtln("dp(%d, %d, %d, %d, %d, %d) = %d", mod, sum, remainder, index, c, f, ans);
        }
        return ans;
      };
      ans += dp(dp, 0, 1, numOfDigit, 1, 1);
    }
    return ans;
  }
};
