int f[10][82][82][82], a[10];

class Solution {
public:
    int calc(int p) {
        if (p == 0) return 0;
        int n = 0;
        while (p) {
            a[n++] = p%10;
            p /= 10;
        }
        int ret = 0;
        for (int i = n-1; i > 0; --i) {
            for (int j = 1; j < 82; ++j) {
                for (int x = 1; x < 10; ++x) {
                    for (int t = 0; t < 82; ++t) {
                        if (j >= x && t*x%j == 0) {
                            ret += f[i-1][j][j-x][t];
                        }
                    }
                }
            }
        }
        // printf("ret=%dn", ret);
        int s = 0, d = 1;
        for (int i = n-1; i >= 0; --i) {
            int y = a[i];
            int x = 0;
            if (i == n-1) x = 1;
            for (; x < y; ++x) {
                for (int j = s+x; j < 82; ++j) {
                    for (int t = 0; t < j; ++t) {
                        if (1LL*d*x*t%j == 0) {
                            ret += f[i][j][j-s-x][t];
                        }
                    }
                }
            }
            s += y;
            d = d*y;
            // printf("ret=%dn", ret);
        }
        if (d%s == 0) ret += 1;
        return ret;
    }

    int beautifulNumbers(int l, int r) {
        if (f[0][1][0][0] == 0) {
            for (int j = 1; j < 82; ++j) {
                f[0][j][0][1%j] = 1;
            }
            for (int i = 1; i < 10; ++i) {
                for (int j = 1; j < 82; ++j) {
                    for (int k = 0; k < 82; ++k) {
                        for (int t = 0; t < 82; ++t) {
                            for (int x = 0; x < 10; ++x) {
                                if (k-x >= 0) f[i][j][k][t*x%j] += f[i-1][j][k-x][t];
                            }
                        }
                    }
                }
            }
        }
        // printf("%d %dn", calc(r), calc(l-1));
        return calc(r)-calc(l-1);
    }
};

