class Solution {
public:
    vector<unordered_map<int, unordered_map<int, int>>> cache;
    void init(int digit) {
        cache = vector<unordered_map<int, unordered_map<int, int>>>(10,
            unordered_map<int, unordered_map<int, int>>{});

        cache[0][0][1] = 1;
        for (int i = 0; i < 10; ++i) {
            cache[1][i][i] = 1;
        }

        for (int i = 2; i < digit; ++i) {
            for (int j = 0; j < 10; ++j) {
                for (auto& pr : cache[i - 1]) {
                    auto sum = pr.first;
                    for (auto& pr2 : pr.second) {
                        auto product = pr2.first;
                        cache[i][j + sum][j * product] += pr2.second;
                    }
                }
            }
        }
    }
    int count(int x) {
        //cout << "x = " << x << endl;
        int result = 0;
        if (x == 1000000001) {
            --x;
            ++result;
        }
        if (x == 1000000000) {
            --x;
            ++result;
        }
        vector<int> digits;
        while (x > 0) {
            digits.push_back(x % 10);
            x /= 10;
        }

        for (int len = 1; len < digits.size(); ++len) {
            for (int i = 1; i < 10; ++i) {
                for (auto& pr : cache[len - 1]) {
                    for (auto& pr2 : pr.second) {
                        if ((pr2.first * i) % (pr.first + i) == 0) {
                            result += pr2.second;
                        }
                    }
                }
            }
        }

        int sum_r = 0, product_r = 1;
        for (int i = digits.size() - 1; i >= 0; --i) {
            int d = 0;
            if (i == digits.size() - 1) d = 1;
            for (; d < digits[i]; ++d) {
                for (auto& pr : cache[i]) {
                    for (auto& pr2 : pr.second) {
                        if ((pr2.first * d * product_r) % (pr.first + d + sum_r) == 0) {
                            //cout << i << " " << d << " " << (pr2.first * d * product_r) << " " << (pr.first + d + sum_r) << " " <<  pr2.second << endl;
                            result += pr2.second;
                        }
                    }
                }
            }

            sum_r += d;
            product_r *= d;

            //cout << sum_r << " * " << product_r << endl;
        }
        //cout << "result = " << result << endl;
        return result;
    }
    int beautifulNumbers(int l, int r) {
        ++r;
        int x = r;
        vector<int> digits;
        while (x > 0) {
            digits.push_back(x % 10);
            x /= 10;
        }
        init(min(9, (int)digits.size()));
        return count(r) - count(l);
    }
};

