class Solution {
public:
    int beautifulNumbers(int l, int r) {
        auto solve = [](string x) -> int
        {
            if (x == "0") return 0;
            int n = x.length();
            map<array<int, 5>, int> mp;
            auto dfs = [&](auto &dfs, int cur, int mul, int add, int lim, int leading_zero) -> int
            {
                if (mp.count({cur, mul, add, lim, leading_zero}))
                    return mp[{cur, mul, add, lim, leading_zero}];
                if (cur == n) {
                    if (leading_zero) return 0;
                    assert(add > 0);
                    return mul % add == 0;
                }
                int res = 0;
                for (int c = 0; c <= (lim ? x[cur] - '0' : 9); c++) {
                    int _mul = mul * (leading_zero and c == 0 ? 1 : c);
                    int _add = add + c;
                    int _lim = lim && c == x[cur] - '0';
                    int _leading_zero = leading_zero && c == 0;
                    res += dfs(dfs, cur + 1, _mul, _add, _lim, _leading_zero);
                }
                return mp[{cur, mul, add, lim, leading_zero}] = res;
            };
            return dfs(dfs, 0, 1, 0, 1, 1);
        };

        return solve(to_string(r)) - solve(to_string(l - 1));
    }
};

