@cache
def calculate(val):
    arr = [int(x) for x in str(val)]
    n = len(arr)

    @cache
    def dp(idx, curprod, cursum, at_limit=True):

        if idx == n:
            if cursum == 0:
                return 0
            if curprod % cursum == 0:
                return 1
            return 0

        res = 0
        for val in range(10):
            if val == 0 and idx == 0:
                continue
            if at_limit and val == arr[idx]:
                res += dp(idx + 1, curprod * val, cursum + val, True)
                break
            res += dp(idx + 1, curprod * val, cursum + val, False)

        return res

    res = dp(0, 1, 0, True)
    return res

def actual_calculate(val):
    if val == 0:
        return 0
    res = 0
    for i in range(1, 10):
        if val >= 10**i:
            res += calculate(10**i - 1)
        else:
            res += calculate(val)
            break
    return res


