use std::collections::HashMap;

impl Solution {
    fn digit_dp(
        digits: &[i32],
        low_digits: &[i32],
        r_nums: &[i32],
        l_nums: &[i32],
        powers: &[i32],
        memo: &mut HashMap<(usize, bool, bool, i32, i32), i32>,
        pos: usize,
        is_upper: bool,
        is_lower: bool,
        product: i32,
        sum: i32,
    ) -> i32 {
        if pos == digits.len() {
            return if sum != 0 && product % sum == 0 { 1 } else { 0 };
        }

        if let Some(&ans) = memo.get(&(pos, is_upper, is_lower, product, sum)) {
            return ans;
        }

        let mut ans = 0;
        let upper_bound = if is_upper { digits[pos] } else { 9 };
        let lower_bound = if is_lower { low_digits[pos] } else { 0 };
        for i in lower_bound..=upper_bound {
            if i == 0 && sum != 0 {
                let mut remaining = 0;
                if is_upper && i == upper_bound {
                    remaining = r_nums[pos + 1] + 1;
                    //for j in pos + 1..digits.len() {
                    //    remaining = remaining * 10 + digits[j];
                    //}
                    //remaining += 1;
                } else {
                    //remaining += 10_i32.pow((digits.len() - pos - 1) as u32);
                    remaining += powers[(digits.len() - pos - 1) as usize];
                }

                let mut deduction = 0;
                if is_lower && i == lower_bound {
                    deduction = l_nums[pos + 1];
                    //for j in pos + 1..low_digits.len() {
                    //    deduction = deduction * 10 + low_digits[j];
                    //}
                }
                ans += remaining - deduction;
                continue;
            }
            ans += Self::digit_dp(
                digits,
                low_digits,
                r_nums,
                l_nums,
                powers,
                memo,
                pos + 1,
                is_upper && i == upper_bound,
                is_lower && i == lower_bound,
                product * if sum == 0 && i == 0 { 1 } else { i },
                sum + i,
            );
        }

        memo.insert((pos, is_upper, is_lower, product, sum), ans);
        ans
    }

    pub fn beautiful_numbers(mut l: i32, mut r: i32) -> i32 {
        let mut r_digits = Vec::new();
        while r != 0 {
            r_digits.push(r % 10);
            r /= 10;
        }
        r_digits.reverse();

        let mut r_nums = vec![0; r_digits.len() + 1];
        let mut r = 1;
        for i in (0..r_digits.len()).rev() {
            r_nums[i] = r * r_digits[i] + r_nums[i + 1];
            r *= 10;
        }

        //println!("{:?}", r_nums);

        let mut l_digits = Vec::new();
        if l == 0 {
            l_digits.push(0);
        } else {
            while l != 0 {
                l_digits.push(l % 10);
                l /= 10;
            }
        }
        while l_digits.len() < r_digits.len() {
            l_digits.push(0);
        }
        l_digits.reverse();

        let mut l_nums = vec![0; l_digits.len() + 1];
        let mut l = 1;
        for i in (0..l_digits.len()).rev() {
            l_nums[i] = l * l_digits[i] + l_nums[i + 1];
            l *= 10;
        }

        //println!("{:?}", l_nums);

        let mut powers = vec![1; r_digits.len()];
        for i in (0..r_digits.len() - 1) {
            powers[i + 1] = powers[i] * 10;
        }

        //println!("{:?}", powers);

        let mut memo = HashMap::new();

        Self::digit_dp(
            &r_digits, &l_digits, &r_nums, &l_nums, &powers, &mut memo, 0, true, true, 1, 0,
        )
    }
}

