class Solution:

    def beautifulNumbers(self, l: int, r: int) -> int:
        @lru_cache(None)
        def dfs(s, index, cheng, now_sum, limit, chose):
            if index == len(s):
                if not chose:
                    return 0
                return cheng % now_sum == 0
            ans = 0            
            if not chose:
                ans += dfs(s, index + 1, 1, 0, False, False)
            left = 0 if chose else 1
            right = int(s[index]) if limit else 9
            for i in range(left, right + 1):
