class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:        
        def calc2(target):
            if not target:
                return 0
            d = [int(ch) for ch in str(target)][::-1]
            n = len(d)
            m = n * 9
            res = 0
            for L in range(1, n):
                for num in range(1, L * 9 + 1):
                    res += calc(L, L, num, num, 1)
            for num in range(1, m + 1):
                acc = num
                product = 1
                valid = True
                for pos in range(n-1, -1, -1):
                    start = 1 if pos == n - 1 else 0
                    for digit in range(start, d[pos]):
                        if digit > acc:
                            break
                        r = product * digit % num
                        val = calc(pos, n, num, acc - digit, r)
                        #print(target, ':', pos, n, num, acc - digit, r, '->', val)
                        res += val
                    if d[pos] > acc:
                        break
                    acc -= d[pos]
                    product = product * d[pos] % num
            num = sum(d)
            product = 1
            for digit in d:
                product *= digit
            if product % num == 0:
                res += 1
            return res

        n = len(str(r))
        for i in range(1, n + 1):
            m = i * 9
            for tot in range(1, m + 1):
                calc(i, i, tot, tot, 1)

        return calc2(r) - calc2(l - 1)

