class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def cal1(x):
            ds = [int(d) for d in str(x)]
            n = len(ds)

            @cache
            def dp(idx, f, has, s, p) -> int:
                if idx == n:
                    if not has:
                        return 0
                    return 1 if (s != 0 and p % s == 0) else 0
                res = 0
                limit = ds[idx] if f else 9
                for d in range(0, limit + 1):
                    f1 = f and (d == limit)
                    if not has:
                        if d == 0:
                            res += dp(idx + 1, f1, 0, s, p)
                        else:
                            res += dp(idx + 1, f1, 1, s + d, p * d)
                    else:
                        if d != 0:
                            res += dp(idx + 1, f1, 1, s + d, p * d)
                return res

            return dp(0, 1, 0, 0, 1)

        def cal0(x):
            ds = [int(d) for d in str(x)]
            n = len(ds)

            @cache
            def dp(idx, f, has):
                if idx == n:
                    return 1 if has else 0
                res = 0
                limit = ds[idx] if f else 9
                for d in range(limit + 1):
                    f1 = f and (d == limit)
                    if not has:
                        if d == 0:
                            res += dp(idx + 1, f1, 0)
                        else:
                            res += dp(idx + 1, f1, 1)
                    else:
                        if d == 0:
                            continue
                        res += dp(idx + 1, f1, 1)
                return res

            return dp(0, 1, 0)

        def cal(x):
            if x <= 0:
                return 0
            return x - cal0(x) + cal1(x)

        return cal(r) - cal(l - 1)

