class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        m = len(str(r))

        def helper(s):
            n = len(s)

            @functools.lru_cache(None)
            def dfs(idx, isLimit, isPre, cur_sum, cur_mod, target_sum):
                if idx == n:
                    return cur_sum == target_sum and cur_mod == 0
                if (n - idx) * 9 + cur_sum < target_sum:
                    return 0
                res = 0 if isPre else dfs(idx + 1, False, isPre, cur_sum, cur_mod, target_sum)
                low = 0 if isPre else 1
                up = int(s[idx]) if isLimit else 9
                for d in range(low, up + 1):
                    if d + cur_sum > target_sum:
                        break
                    res += dfs(idx + 1, isLimit and int(s[idx]) == d, True, d + cur_sum, ((cur_mod * d) if isPre else d) % target_sum, target_sum)
                return res

            res = 0
            for t in range(m * 9 + 1):
                res += dfs(0, True, False, 0, 0, t)
            dfs.cache_clear()
            return res

        return helper(str(r)) - helper(str(l - 1))

