class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        @cache
        def dp(p, s, k):
            if p == 0:
                return 10 ** k
            if k == 0:
                return 1 if p % s == 0 else 0
            res = 0
            for d in range(10):
                res += dp(p * d, s + d, k - 1)
            return res

        def f(a):
            if a == 0:
                return 0

            A = list(map(int, str(a)))
            n = len(A)
            dp.cache_clear()
            res = 0
            for d in range(1, A[0]):
                res += dp(d, d, n - 1)
            p = s = A[0]
            for i in range(1, n):
                for d in range(A[i]):
                    res += dp(p * d, s + d, n - i - 1)
                for d in range(1, 10):
                    res += dp(d, d, n - i - 1)
                p *= A[i]
                s += A[i]
            res += p % s == 0
            return res

        return f(r) - f(l - 1)

