const int64_t MOD = 1e9+7;
using Key = std::tuple<int, int, int, int, int, int>;
struct KeyHash {
  std::size_t operator()(const Key & key) const {
    auto [a,b,c,d,e,f] = key;
    return a*31l+b*MOD+c*97+d*7+e*11+f*113;
  }
};
unordered_map<Key, int, KeyHash> M;
class Solution {
public:

  int dfs(const string &s, bool firstUse, int cur, int remain, int mod) {
    if (remain<0) return 0;
    if (s.size()*9<remain) return 0;
    if (s.size() == 0) return remain ==0 && cur == 0;
    Key k = {stoi(s), s.size(), (int)firstUse, cur, remain, mod};
    if (M.count(k)) return M[k];
    int tot = 0;
    int ss = s[0]-'0';
    string s9 = string(s.size()-1, '9');
    if (firstUse) {
      for (int i = 0; i <= ss; i++) {
        tot += dfs(i==ss?s.substr(1):s9, firstUse, cur*i%mod, remain-i, mod);
      }
    } else {
      tot += dfs(string(s.size()-1, '9'), false, cur, remain, mod);
      for (int i = 1; i <= ss; i++) {
        tot += dfs(i==ss?s.substr(1):s9, true, cur*i%mod, remain-i, mod);
      }
    }
    return M[k]=tot;
  }


  int gao(int n) {
    int tot = 0;
    for (int i = 1; i <= 81; i++) {
      tot += dfs(to_string(n), false, 1, i, i);
    }
    return tot;
  }


  int beautifulNumbers(int l, int r) {
    return gao(r)-gao(l-1);
  }
};

