        @cache
        def dfs(i, is_num, limit_low, limit_high, s, mul):
            if i == n:
                return mul % s == 0

            lo = int(num1[i]) if limit_low else 0
            hi = int(num2[i]) if limit_high else 9

            res = 0
            for x in range(lo, hi + 1):
                if is_num:
                    res += dfs(i + 1, is_num, limit_low and lo == x, limit_high and hi == x, s + x, mul * x)
                else:
                    if x == 0:
                        res += dfs(i + 1, is_num, limit_low and lo == x, limit_high and hi == x, s, mul)
                    else:
                        res += dfs(i + 1, not is_num, limit_low and lo == x, limit_high and hi == x, s + x, mul * x)
            return res
        return dfs(0, False, True, True, 0, 1)

