class Solution {
public:
    int beautifulNumbers(int l, int r) {
        string s1 = to_string(l), s2 = to_string(r);
        s1 = string(s2.size() - s1.size(), '0') + s1;
        int ret = 0;
        for (int s = 1; s <= 81; s++) {
            memset(f, -1, sizeof(f));
            auto dfs = [&](auto &&dfs, int u, int low, int high, int lead, int p, int sum) {
                if (u == s1.size()) return int(!lead && !p && sum == s);
                if (!low && !high && !lead && f[u][p][sum] != -1) return f[u][p][sum];
                int l = low ? s1[u] - '0' : 0, r = high ? s2[u] - '0' : 9, ret = 0;
                if (lead && s1[u] == '0') ret = dfs(dfs, u + 1, 1, 0, 1, p, sum);
                for (int i = max(l, lead); i <= r; i++) {
                    ret += dfs(dfs, u + 1, low && i == l, high && i == r, 0, p * i % s, sum + i);
                }
                if (!low && !high && !lead) f[u][p][sum] = ret;
                return ret;
            };
            ret += dfs(dfs, 0, 1, 1, 1, 1, 0);
        }
        return ret;
    }
