typedef long long LL;

class Solution {
public:
    int n;
    string s;
    vector<unordered_map<LL,int>>mp;


    int dfs(int pos,int sum,LL mul,int limit,int num){
        if(pos==n){
            if(sum==0)  return 0;
            return mul%sum==0;
        }

        LL bas=(mul<<32)|(sum<<5)|(limit<<2)|num;
        if(mp[pos].count(bas)) return mp[pos][bas];
        int res=0;
        if(!num){
            res=dfs(pos+1,0,1,0,0);
        }
        int up=limit?s[pos]-'0':9;
        for(int i=num?0:1;i<=up;i++){
            res+=dfs(pos+1,sum+i,mul*i,limit&&i==up,1);
        }


        return mp[pos][bas]=res;
    }

    int beautifulNumbers(int l, int r) {
        s=std::to_string(r);
        n=s.size(); mp.resize(n);
        int ans=dfs(0,0,1,1,0);

        // cout<<ans<<endl;
        s=to_string(l-1);
        for(int i=0;i<n;i++)    mp[i].clear();
        n=s.size(); mp.resize(n);


        ans-=dfs(0,0,1,1,0);
        return ans;
    }
};

