class Solution {
    int f(int k) {
        string s = to_string(k);
        int n = s.size();
        vector<map<pair<int,int>, int>> memo(n);
        auto dfs = [&](auto&& dfs, int i, int prod, int mod, bool is_limit, bool is_num) -> int {
            if(i == n) {
                bool check = (mod != 0 && (prod % mod) == 0);
                return check ? 1 : 0;
            }

            if(!is_limit && is_num && memo[i].contains({prod,mod})) {
                return memo[i][{prod, mod}];
            }
            int res = !is_num ? dfs(dfs, i+1, prod, mod, false, false) : 0;
            int upper = is_limit ? s[i] - '0' : 9;
            for(int d=1-is_num; d<=upper; d++) {
                int new_prod = prod * d;
                int new_mod = mod * d;
                res += dfs(dfs, i+1, prod * d, mod + d, is_limit && d == upper, true);
            }
            if(!is_limit) {
                memo[i][{prod, mod}] = res;
            }
            return res;
        };
        return dfs(dfs, 0, 1, 0, true, false);
    }
public:
    int beautifulNumbers(int l, int r) {
        cout << l-1 << " " << f(l-1) << endl;
        cout << r << " " << f(r) << endl;
        return f(r) - f(l-1);
    }
};

