class Solution {
public:
    using INT = __int128;
    int P = 28;
    int pw[10];
    int pos[20], mp[10]{7,5,3,2};
    int cd[10];
    unordered_map<int, int> f[10][100][2];
    vector<int> nums;
    INT fpow(INT a, int b) {
        INT r = 1;
        while(b) {
            if(b & 1) r = a * r;
            b >>= 1;
            a = a * a;
        } return r;
    }
    int modify(int p, int v, int c) {
        return c + pw[p] * v;
    }
    int G(int p, int c) {
        return c / pw[p] % P;
    }
    int check(int add, int c) {
        INT mul = 1;
        for(int i = 0; i < 4; ++i) mul *= fpow(mp[i], G(pos[mp[i]], c));
        return mul % add == 0;
    }
    int dfs(int p, int add, int c0, int c, int lmt, int is_num) {
        if(!~p) {
            if(!is_num) return 0;
            if(c0) return 1;
            return check(add, c);
        }
        if(!lmt && is_num && f[p][add][c0].count(c)) return f[p][add][c0][c];
        int res = 0;
        if(!is_num) res = dfs(p - 1, add, c0, c, 0, 0);
        int ed = lmt ? nums[p] : 9;
        for(int i = !is_num; i <= ed; ++i) {
            int t = c;
            res += dfs(p - 1, add + i, c0 || !i, c + cd[i], lmt && i == ed, 1);
        }
        if(!lmt && is_num) f[p][add][c0][c] = res;
        return res;
    }
    int dp(int v) {
        if(!v) return 0;
        nums.clear();
        while(v) nums.push_back(v % 10), v /= 10;
        for(int i = 0; i < 10; ++i)
            for(int j = 0; j < 100; ++j)
                for(int k = 0; k < 2; ++k)
                    unordered_map<int, int>().swap(f[i][j][k]);
        return dfs(nums.size() - 1, 0, 0, 0, 1, 0);
    }
    int beautifulNumbers(int l, int r) {
        memset(pos, 0xff, sizeof pos);
        pos[7] = 0, pos[5] = 1, pos[3] = 2, pos[2] = 3;
        pw[0] = 1;
        for(int i = 1; i <= 4; ++i) pw[i] = pw[i - 1] * P;
        for(int i = 1; i <= 9; ++i) {
            for(int j = 0; j < 4; ++j) {
                int t = i, c = 0;
                while(t % mp[j] == 0) t /= mp[j], c++;
                cd[i] += modify(j, c, 0);
            }
        }
        return dp(r) - dp(l - 1);
    }
};

