typedef long long ll;
string s;
unordered_map<string, int> memo;
class Solution {
public:
    ll dfs(int po, int t, bool fir, int sum, ll pr) {
        if (po == s.size()) {
            if (!fir) return 0;
            return (pr % sum == 0) ? 1LL : 0LL;
        }
        string k = to_string(po) + " " + to_string(t) + " " + to_string(fir) +  " " + to_string(sum) + " " + to_string(pr);
        if (memo.count(k)) return memo[k];
        ll res = 0;
        int lit = t ? s[po] - '0' : 9;
        for (int d = 0; d <= lit; d++) {
            int nt = t && (d == lit);
            bool nfir = fir || (d != 0);
            int nsum = sum;
            ll npr = pr;
            if (nfir) {
                nsum += d;
                if (npr == 0 || d == 0) npr = 0;
                else npr *= d;
            }
            res += dfs(po + 1, nt, nfir, nsum, npr);
        }
        memo[k] = res;
        return res;
    }
    ll beautycnt(ll n) {
        s = to_string(n);
        memo.clear();
        return dfs(0, 1, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        return (int)(beautycnt(r) - beautycnt(l - 1));
    }
};

