class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        @lru_cache(None)
        def dp(index, islimit, preSum, preProd, numStr):
            if index == len(numStr):
                return int(preProd%preSum == 0) if preSum != 0 else 0
            limit = int(numStr[index]) if islimit else 9
            total = 0
            for v in range(0, limit+1):
                if v != 0:
                    curProd = v if preProd is None else preProd*v
                else:
                    curProd = None if preProd is None else preProd*v
                total += dp(index+1, islimit and (v == limit), preSum+v, curProd, numStr)
            return total
        return dp(0, True, 0, None, str(r)) - dp(0, True, 0, None, str(l-1))

