class Solution {
public:
    unordered_map<string, int> m;
    int dfs(int i, string& bound, int le, string& cnt, int has1) {
        if(i == bound.size()) {
            if(cnt[0] > '0') {
                return 1;
            } else {
                int m = 1, s = 0;
                for(int i = 1; i < 10; ++i) {
                    for(int j = 0; j < cnt[i] - '0'; ++j) {
                        m *= i;
                    }
                    s += i * (cnt[i] - '0');
                }
                if(s == 0) return 1;
                if(m % s == 0) return 1;
                return 0;
            }
        }
        cnt[11] = le + '0';
        if(m.find(cnt) != m.end()) {
            return m[cnt];
        }
        int res = 0;
        for(int ne = 0; ne < 10; ++ne) {
            if(le && ne > bound[i] - '0') {
                break;
            }
            if(ne > 0 || has1)
                cnt[ne]++;
            cnt[10]++;
            res += dfs(i + 1, bound, le && (ne == bound[i] - '0'), cnt, has1 || ne > 0);
            if(ne > 0 || has1)
                cnt[ne]--;
            cnt[10]--;
        }
        m[cnt] = res;
        return res;
    }

    int f(int x) {
        m.clear();
        string cnt = "000000000000";
        string bound = to_string(x);
        return dfs(0, bound, 1, cnt, 0);
    }

    int beautifulNumbers(int l, int r) {
        return f(r) - f(l-1);
    }
};

