func beautifulNumbers(l int, r int) int {
	leftCount := 0
	if l > 1 {
		leftCount = countBeautiful(l - 1)
	}
	return int(countBeautiful(r) - leftCount)
}
func countBeautiful(x int) int {
	s = strconv.Itoa(x)
	n = len(s)
	memo = make(map[string]int)
    return f(0, 0, 1, true, false)
}
// dp 
func f(i, sum, prod int, isLimit, isNum bool) (res int) {
    if i == n{
        if isNum && sum > 0 && prod % sum == 0 {
            return 1
        }
        return 0
    }
    if !isLimit && isNum {
        key := fmt.Sprintf("%d_%d_%d_%t", i, sum, prod, isNum)
        if val, ok := memo[key]; ok {
            return val
        }
    }
    if !isNum {
        res += f(i+1, sum, prod, false, false)
    }
    up := 9
    if isLimit {
        up = int(s[i] - '0')
    }
    start := 0
    if !isNum {
        start = 1
    }
    for d := start; d <= up ;d++ {
        curIsLimit := isLimit && (d == up)
        var newSum, newProd int
        if !isNum {
            newSum = d
            newProd = d
        }else{
            newSum = sum + d
            newProd = prod * d
        }
        res += f(i+1,newSum,newProd,curIsLimit,true)
    }
    if !isLimit && isNum {
        key := fmt.Sprintf("%d_%d_%d_%t",i, sum, prod, isNum)
        memo[key] = res
    }
    return res
}

