class Solution {
    public int beautifulNumbers(int l, int r) {
        int v1 = calc(r);
        int v2 = calc(l-1);
        // System.out.println(String.format("%d %d",v1,v2));
        return v1-v2;
    }
    int calc(int num) {
        HashMap<Integer,int[]> hm = new HashMap<>();
        int L=82;
        char[] ch = Integer.toString(num).toCharArray();
        int n = ch.length;
        int res = 0;
        int m=1,s=0;
        for(int i=0;i<n;i++) {
            int d = ch[i]-'0';
            HashMap<Integer,int[]> hm1 = new HashMap<>();
            for(int j=0;j<10;j++) {
                for(Integer key:hm.keySet()) {
                    int v = key*j;
                    int[] arr1 = hm.get(key);
                    int[] arr2 = hm1.getOrDefault(v,new int[L]);
                    for(int k=j;k<L;k++) {
                        arr2[k] += arr1[k-j];
                    }
                    hm1.put(v,arr2);
                }
                if(j<d && (i!=0 || j>0)) {
                    int key = m*j;
                    int[] arr = hm1.getOrDefault(key,new int[L]);
                    arr[s+j] += 1;
                    hm1.put(key,arr);
                }
                if(i>0 && j>0) {
                    int key = j;
                    int[] arr = hm1.getOrDefault(key,new int[L]);
                    arr[j] += 1;
                    hm1.put(key,arr);
                }
            }                
            m *= d;
            s += d;
            hm = hm1;
        }
        int[] arr = hm.getOrDefault(m,new int[L]);
        arr[s] += 1;
        hm.put(m,arr);
        for(Integer key:hm.keySet()) {
            int[] t = hm.get(key);
            for(int i=1;i<L;i++) {
                if(key % i==0 && t[i]>0) {
                    res += t[i];
                    // System.out.println(String.format("%d %d %d",key,i,t[i]));
                }
            }
        }
        // System.out.println(String.format("c%d %d",num,res));
        return res;
    }
}

