class Solution {
    private int[][] gcdDict=new int[82][10];
    public int beautifulNumbers(int l, int r) {
        for(int i=1;i<gcdDict.length;i++){
            for(int j=0;j<10;j++){
                if(j==0){
                    gcdDict[i][j]=1;
                }else {
                    gcdDict[i][j]=i/gcd(i,j);
                }
            }
        }
        DigitDp digitDp = new DigitDp();
        return digitDp.cal(l,r);
    }

    private int gcd(int a, int b) {
        if (a <= b) {
            a = a ^ b;
            b = a ^ b;
            a = a ^ b;
        }
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    class DigitDp{
        //{pos,sum,modLeft}
        private int[][][][]cache;
        private char[] charsHigh;
        private char[] charsLow;

        private int cal(int low,int high) {
            String lowStr=String.valueOf(low);
            String highStr=String.valueOf(high);

            if (high == 0) {
                return 0;
            }
            this.charsHigh = String.valueOf(high).toCharArray();
            this.charsLow = new char[charsHigh.length];
            int index = 0;
            while (index < highStr.length() - lowStr.length()) {
                charsLow[index++] = '0';
            }
            for (int i = 0; i < lowStr.length(); i++) {
                charsLow[index++] = lowStr.charAt(i);
            }
            int min = 1, max = charsHigh.length * 9;
            int res=0;
            for (int i = min; i <= max; i++) {
                this.cache = new int[charsHigh.length][i + 1][i + 1][2];
                res+=doCal(0, i,i,false, false,true);
            }
            return res;
        }

        private int doCal(int pos,int sum,int leftMod, boolean hasSmall,boolean hasBig,boolean headZero) {
            if (pos == charsHigh.length) {
                if(sum==0&&leftMod==1){
                    return 1;
                }else {
                    return 0;
                }
            }
            int res=0;
            if (hasSmall && hasBig) {
                res = cache[pos][sum][leftMod][headZero ? 1 : 0];
            }
            if (res != 0) {
                return res - 1;
            }

            for (int i = 0; i <= 9; i++) {
                if (!hasBig && i < charsLow[pos] - '0') {
                    continue;
                }

                if (!hasSmall && i > charsHigh[pos] - '0') {
                    break;
                }
                int leftSum=sum-i;
                if(leftSum<0){
                    break;
                }
                boolean nextSmall = hasSmall || i < charsHigh[pos] - '0';
                boolean nextBig = hasBig || i > charsLow[pos] - '0';
                boolean nextHeadZero = headZero && i == 0 && pos != charsHigh.length - 1;
                int nextGcd=nextHeadZero?leftMod:gcdDict[leftMod][i];
                res+=doCal(pos+1,leftSum,nextGcd,nextSmall,nextBig,nextHeadZero);
            }
            if(hasSmall&&hasBig) {
                cache[pos][sum][leftMod][headZero ? 1 : 0] = res + 1;
            }
            return res;
        }
    }
}

