                while(x % f[i] == 0) { num[y][i]++; x /= f[i]; }
            }
        }
        return fun(r) - fun(l-1);
    }
    int fun(int n) {
        memset(dp, -1, sizeof(dp)); memset(digit, 0, sizeof(digit)); m = 0;
        for( ; n ; n/=10) digit[m++] = n%10;
        return solve(m-1, 0, 1, 1, 0, 0, 0, 0, 0);
    }
    int solve(int pos, int sum, int lmt, int lz, int zero, int n2, int n3, int n5, int n7) {
        if(pos == -1) {
            if(sum == 0) return 0;
            if(zero) return 1;
            vector<int> num = {n2, n3, n5, n7}, tmp(4, 0);
            for(int i =0; i< f.size(); i++) {
                while(sum != 1 && sum % f[i] == 0) { tmp[i]++; sum /= f[i]; }
            }
            if(sum != 1) return 0;
            for(int i=0; i<num.size(); i++) {
                if(tmp[i] > num[i]) return 0;
            }
            return 1;
        }
        int pp = lmt << 2 | lz << 1 | zero;
        int& ret = dp[pos][sum][pp][n2][n3][n5][n7];
        if(ret != -1) return ret;
        int up = lmt? digit[pos] : 9;  ret = 0;
        for(int k = 0; k<=up; k++) {
            ret += solve(pos-1, sum + k, lmt && k == digit[pos], lz && k==0 , (!lz && k==0) || zero, min(6, n2 + num[k][0]), min(4, n3 + num[k][1]), min(2, n5 + num[k][2]), min(2, n7 + num[k][3]));
        }
        return ret;
    }
    int dp[10][82][8][7][5][3][3], digit[11], m; vector<int> f = {2, 3, 5, 7}; vector<vector<int>> num;
};

