class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        @cache
        def dfs(num_str, pos, leading_zero, cur_sum, cur_product, tight):
            if pos == len(num_str):
                if leading_zero: return 0
                return 1 if (cur_sum != 0 and cur_product % cur_sum == 0) else 0

            limit = int(num_str[pos]) if tight else 9
            count = 0

            for digit in range(0, limit + 1):
                new_tight = tight and (digit == limit)
                new_leading_zero = leading_zero and (digit == 0)

                if new_leading_zero:
                    new_sum = cur_sum
                    new_product = cur_product
                else:
                    new_sum = cur_sum + digit
                    if leading_zero:
                        new_product = digit
                    else:
                        new_product = cur_product * digit

                count += dfs(num_str, pos + 1, new_leading_zero, new_sum, new_product, new_tight)

            return count

        def count_up_to(num):
            if num < 0:
                return 0
            num_str = str(num)
            return dfs(num_str, 0, True, 0, 1, True)

        count_r = count_up_to(r)
        dfs.cache_clear()
        count_l = count_up_to(l - 1)

        return count_r - count_l

