class Solution:
    def cal(self, num):
        num = str(num)
        n = len(num)
        @cache
        def dfs(i, isUpper, isZero, m, s):
            nonlocal n
            if m == 0 and isUpper is False:
                return 10 ** (n - i)

            if i == n:
                return int(s == 0 or m % s == 0)

            res = 0
            for k in range(10 if isUpper is False else int(num[i]) + 1):
                res += dfs(i + 1,
                           isUpper and k == int(num[i]),
                           isZero and k == 0,
                           m * (1 if isZero and k == 0 else k),
                           s + k)

            return res

        return dfs(0, True, True, 1, 0)

    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.cal(r) - self.cal(l - 1)

