class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        # def count(x):

        def count(x: int) -> int:
            s = str(x)
            n = len(s)

            @cache
            def dfs(i, sum, p, is_limit, is_num, has_zero):
                if i == n:
                    if not is_num:
                        return 0
                    if has_zero:
                        return 1
                    if sum != 0 and p % sum == 0:
                        return 1
                    return 0

                res = 0
                limit = int(s[i]) if is_limit else 9
                for d in range(0, limit + 1):
                    cur_limit = is_limit and (d == limit)
                    if not is_num:
                        if d == 0:
                            res += dfs(i + 1, 0, 1, cur_limit, False, False)
                        else:
                            res += dfs(i + 1, d, d, cur_limit, True, False)
                    else:
                        if d == 0:
                            res += dfs(i + 1, sum, 0, cur_limit, True, True)
                        else:
                            if has_zero:
                                res += dfs(i + 1, sum + d, 0, cur_limit, True, True)
                            else:
                                res += dfs(i + 1, sum + d, p * d, cur_limit, True, False)
                return res
            return dfs(0, 0, 1, True, False, False)

        return count(r) - count(l - 1)

