class Solution {
public:
    int beautifulNumbers(int l, int r) {
        memo.assign(10, std::vector<std::vector<int>>(100, std::vector<int>(81, -1)));
        return solve(r) - solve(l - 1);
    }
    string s;
    vector<std::vector<std::vector<int>>> memo;
    int target_sum;

    int dfs(int pos, int digit_sum, int product_mod, bool is_limit, bool is_num) {
        if (pos == s.length()) {
            return is_num && digit_sum == target_sum && product_mod == 0;
        }
        if (!is_limit && is_num && memo[pos][digit_sum][product_mod] != -1) {
            return memo[pos][digit_sum][product_mod];
        }
        int res = 0;
        int up = is_limit ? (s[pos] - '0') : 9;
        for (int digit = 0; digit <= up; ++digit) {
            int new_digit_sum = digit_sum + digit;
            int new_product_mod = digit != 0 ? (product_mod * digit) % target_sum : (is_num ? 0 : 1 % target_sum);
            bool new_is_limit = is_limit && digit == up;
            bool new_is_num = is_num || digit != 0;
            res += dfs(pos + 1, new_digit_sum, new_product_mod, new_is_limit, new_is_num);
        }

        if (!is_limit && is_num) {
            memo[pos][digit_sum][product_mod] = res;
        }
        return res;
    }

    int solve(int n) {
        s = to_string(n);
        int total = 0;
        for (int i = 1; i < 82; ++i) {
            target_sum = i;
            for (int p = 0; p < s.length(); ++p) {
                for (int d = 0; d < 100; ++d) {
                    for (int m = 0; m < target_sum; ++m) {
                        memo[p][d][m] = -1;
                    }
                }
            }
            total += dfs(0, 0, 1 % target_sum, true, false);
        }
        return total;
    }
};

