var beautifulNumbers = function (l, r) {
  function getCnt(num) {
    const n = String(num);
    const cache = new Map();
    const getKey = (i, isLimit, isNum, g, sum) => {
      return `${i},${isLimit},${isNum},${g},${sum}`;
    };

    /**
     * i: i
     * isLimit: i-1ni-1
     * isNum: 111
     * s
     */
    function dp(i, isLimit, isNum, g, sum) {
      // 
      if (i >= n.length) {
        return (((g % sum) == 0) && isNum) ? 1 : 0
      }
      const k = getKey(i, isLimit, isNum, g, sum);

      // 
      if (cache.has(k)) {
        return cache.get(k);
      }

      let ret = 0;
      // nisLimit
      if (!isNum) {
        ret = dp(i + 1, false, false, g, sum);
      }
      // 
      const max = isLimit ? Math.min(Number(n[i]), 9) : 9;
      const min = isNum ? 0 : 1;
      for (let j = min; j <= max; j++) {
        ret += dp(i + 1, isLimit && j == n[i], true, g * j, sum + j);
      }
      cache.set(k, ret);
      return ret;
    }

    return dp(0, true, false, 1, 0);
  }

  console.log(getCnt(r), getCnt(l - 1));

  return getCnt(r) - getCnt(l - 1);
};

