class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count(r_val):
            if r_val < 1:
                return 0
            r_str = str(r_val)
            count_without_zero = self.count_without_zero(r_str)
            count_contains_zero = r_val - count_without_zero
            count_non_zero_valid = 0
            max_s = 9 * len(r_str)
            for s in range(1, max_s + 1):
                cnt = self.count_s(s, r_str)
                count_non_zero_valid += cnt
            return count_contains_zero + count_non_zero_valid
        return count(r) - count(l - 1)
    def count_without_zero(self, n_str):
        digits = list(map(int, n_str))
        m = len(digits)
        from functools import lru_cache
        @lru_cache(maxsize=None)
        def dp(pos, tight, is_num):
            if pos == m:
                return 1 if is_num else 0
            limit = digits[pos] if tight else 9
            total = 0
            for d in range(0, limit + 1):
                new_tight = tight and (d == limit)
                new_is_num = is_num or (d != 0)
                if new_is_num:
                    if d == 0:
                        continue 
                    total += dp(pos + 1, new_tight, new_is_num)
                else:
                    total += dp(pos + 1, new_tight, new_is_num)
            return total
        return dp(0, True, False)
    def count_s(self, s, n_str):
        if s == 0:
            return 0
        digits = list(map(int, n_str))
        m = len(digits)
        from functools import lru_cache
        @lru_cache(maxsize=None)
        def dp(pos, sum_so_far, prod_mod, tight, is_num):
            if pos == m:
                return 1 if (is_num and sum_so_far == s and prod_mod == 0) else 0
            res = 0
            max_d = digits[pos] if tight else 9
            for d in range(0, max_d + 1):
                new_tight = tight and (d == max_d)
                new_is_num = is_num or (d != 0)
                if not new_is_num:
                    res += dp(pos + 1, sum_so_far, prod_mod, new_tight, new_is_num)
                else:
                    if d == 0:
                        continue
                    new_sum = sum_so_far + d
                    new_prod_mod = (prod_mod * d) % s
                    remaining = m - pos - 1
                    if new_sum > s or (new_sum + remaining * 9 < s):
                        continue
                    res += dp(pos + 1, new_sum, new_prod_mod, new_tight, new_is_num)
            return res

        return dp(0, 0, 1, True, False)
